
/*eslint-env node */
module.exports = function () {
  
    this.cache = {};
    
    this.add = function (url, dir, headers, data) {
        var item = {
            url: url,
            dir: dir,
            headers: headers,
            data: data
        };
        
        this.cache[url] = item;
        
        return item;
    };
    
    this.get = function (url) {
        return this.cache[url];
    };
  
};
